var module_53124044076 = void $(document).ready((() => {
    $(".overview-map__point").each((function() {
        const locationId = $(this).data("locationId");
        $(this).hover((() => {
            $(`#${locationId}`).addClass("overview-places__item--active"), $(this).addClass("overview-map__point--active");
            const itemPos = $(`#${locationId}`).position();
            $(`#${locationId}`).parent().stop().animate({
                scrollLeft: itemPos.left - 20
            })
        }), (() => {
            $(`#${locationId}`).removeClass("overview-places__item--active"), $(this).removeClass("overview-map__point--active")
        }))
    }));
    const $map = $(".overview-map");
    $(".overview-places__item").each((function() {
        const locationId = $(this).attr("id"),
            $item = $(".overview-map__point[data-location-id='" + locationId + "']");
        $(this).hover((() => {
            if ($item.addClass("overview-map__point--active"), $(window).outerWidth() < 1024) {
                const itemPos = $item.position();
                $map.stop().animate({
                    scrollLeft: itemPos.left - $map.width() / 2
                })
            }
        }), (() => {
            $item.removeClass("overview-map__point--active")
        }))
    }))
}));