$(document).ready((function() {
    const $ribbons = $(".ribbon");
    $ribbons.length && $ribbons.each((function() {
        const ribbonId = "ribbon_" + $(this).data("id");
        if (!Cookies.get(ribbonId)) {
            const completionEvent = new Event("completedRibbon");
            $(this).stop().slideDown(), $(this).find(".ribbon__close").click((function() {
                $(this).parent().stop().slideUp({
                    complete: function() {
                        $(this).parent()[0].dispatchEvent(completionEvent)
                    }
                }), Cookies.set(ribbonId, !0)
            }))
        }
    }))
}));
